  // App.js
  import React, { Component } from 'react';
  import './App.css';

  class App extends Component {
    constructor(props) {
      super(props);
      this.state = {
        email: '',
        saveEmail: '',
      };
    }

    changeEmail(value) {
      this.setState({ email: value });
    }

    changeSaveEmail(value) {
      this.setState({ saveEmail: value, email: '' });
    }

    render() {
      const { email, saveEmail } = this.state;
      return (
<ValidEmail email={saveEmail}/>
      );
    }
  }

  export default App;
